select * from V_OA_AM_CategorySelectCREATE OR REPLACE VIEW V_OA_AM_CATEGORYSELECT ASselect c.fid,       fc.ffondsid,       f.ffondsno,       f.ffondsname,       c.fcategoryno,       c.fcategoryname,       c.fcategoryurl,       c.fCategoryURLName,       c.fparentid,       c.fkeeplimit,       c.fkeeplimitname,       c.fbscategoryno,       0 as version  from OA_AM_fondscategory fc  join OA_AM_category c on c.fid = fc.fcategoryid                       and c.fusestatus = 1  join OA_AM_fonds f on f.fid = fc.ffondsid                    and f.fallcategory = 0                    and f.fusestatus = 1union (select c.fid,       f.fid,       f.ffondsno,       f.ffondsname,       c.fcategoryno,       c.fcategoryname,       c.fcategoryurl,       c.fCategoryURLName,       c.fparentid,       c.fkeeplimit,       c.fkeeplimitname,       c.fbscategoryno,       0 as version  from OA_AM_fonds f  join OA_AM_category c on c.fParentid = '_is_root_'                       and c.fusestatus = 1 and f.fallcategory = 1)union (select fid,              null,              null,              null,              fcategoryno,              fcategoryname,              fcategoryurl,              fCategoryURLName,              fparentid,              fkeeplimit,              fkeeplimitname,              fbscategoryno,              0 as version         from OA_AM_category        where fusestatus = 1 and fParentID <> '_is_root_') order by fcategoryno;